label("$B$",B,S);
label("$C$",C,SE);
label("$D$",D,NW);
label("$E$",E,W);
label("$F$",F,SE);
label("$G$",G,NE);
label("$H$",H,NW);
label("$M$",M,SW);
label("$N$",N,dir(0));
label("$P$",P,S);
label("$Q$",Q,NW);
draw(M--D--N,dashed);
draw(M--P--N);
draw(P--B);
draw(M--Q);
